mapper.select:
select * from ( SELECT m.id, m.machine_code, CONCAT( area.province, area.city, area.district, if(area.circle='其他','',area.circle),IFNULL( l.mall,""),IFNULL( l.name,"") ) AS local_desc, date_format(ap.start_time,'%Y-%m-%d %H:%i:%s') AS start_time, date_format(ap.end_time,'%Y-%m-%d %H:%i:%s') AS end_time, a.name AS activityName, CASE WHEN ap.start_time > NOW() THEN '1' WHEN ap.start_time < NOW() and ap.end_time > NOW() THEN '2' ELSE null END state, l.area_code FROM inno72_machine m LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area on area.code=l.area_code LEFT JOIN inno72_activity_plan_machine apm on apm.machine_id = m.id LEFT JOIN inno72_activity_plan ap ON ap.id = apm.activity_plan_id and ap.is_delete = 0 and NOW() < ap.end_time
AND ( #{startTime} <= ap.end_time AND #{endTime} >= ap.start_time )
LEFT JOIN inno72_activity a ON a.id = ap.activity_id where m.machine_status ='4' and LEFT(l.area_code,#{num})='${code}'
AND m.machine_code=#{machineCode}
) o order by area_code
