mapper.select:
SELECT g.goods_name AS goodsName, m.machine_code AS machineCode, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localDesc, g.before_num AS beforeNum, g.add_num AS afterNum, IFNULL(g5.num,0) AS num, DATE_FORMAT(g.submit_time,'%Y-%m-%d') AS date FROM inno72_machine_goods_count g LEFT JOIN inno72_machine_goods_count g5 ON g5.machine_id=g.machine_id AND g5.goods_id=g.goods_id AND g5.type=1 AND DATE_FORMAT(g5.submit_time,'%Y-%m-%d') = DATE_FORMAT(g.submit_time,'%Y-%m-%d') LEFT JOIN inno72_machine m ON m.id=g.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE g.type !=1 and g.submit_time >= '2018-11-14' and g.submit_time >= #{beginTime} and g.submit_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or g.goods_name LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
ORDER BY DATE_FORMAT(g.submit_time,'%Y-%m-%d') DESC,g.add_num DESC,g.num DESC

SELECT g.goods_name AS goodsName, m.machine_code AS machineCode, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localDesc, g.before_num AS beforeNum, g.add_num AS afterNum, IFNULL(g5.num,0) AS num, DATE_FORMAT(g.submit_time,'%Y-%m-%d') AS date FROM inno72_machine_goods_count g LEFT JOIN inno72_machine_goods_count g5 ON g5.machine_id=g.machine_id AND g5.goods_id=g.goods_id AND g5.type=1 AND DATE_FORMAT(g5.submit_time,'%Y-%m-%d') = DATE_FORMAT(g.submit_time,'%Y-%m-%d') LEFT JOIN inno72_machine m ON m.id=g.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE g.type !=1 and g.submit_time >= '2018-11-14' and g.submit_time >= #{beginTime} and g.submit_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or g.goods_name LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
ORDER BY DATE_FORMAT(g.submit_time,'%Y-%m-%d') DESC,g.add_num DESC,g.num DESC LIMIT 40000

SELECT s.id,m.id AS machineId,m.machine_code AS machineCode,DATE_FORMAT(s.create_time,'%Y-%m-%d') AS date, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localeStr FROM inno72_supply_channel_order s LEFT JOIN inno72_machine m ON m.id=s.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE 1=1 and LEFT(l.area_code,#{num}) = LEFT(#{code},#{num}) and s.create_time > #{beginTime} and s.create_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
GROUP BY s.machine_id,DATE_FORMAT(s.create_time,'%Y-%m-%d') ORDER BY s.create_time DESC
