mapper.select:
SELECT s.id,m.machine_code AS machineCode,DATE_FORMAT(s.create_time,'%Y-%m-%d') AS date, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localeStr FROM inno72_supply_channel_order s LEFT JOIN inno72_machine m ON m.id=s.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE and LEFT(l.area_code,#{num}) = LEFT(#{code},#{num}) and s.create_time > #{beginTime} and s.create_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
GROUP BY s.machine_id,date ORDER BY date DESC

SELECT s.id,m.machine_id AS machineId,m.machine_code AS machineCode,DATE_FORMAT(s.create_time,'%Y-%m-%d') AS date, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localeStr FROM inno72_supply_channel_order s LEFT JOIN inno72_machine m ON m.id=s.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE and LEFT(l.area_code,#{num}) = LEFT(#{code},#{num}) and s.create_time > #{beginTime} and s.create_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
GROUP BY s.machine_id,date ORDER BY date DESC
