mapper.select:
SELECT hi.id, g.`name` AS goodsName, m.machine_code AS machineCode, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localDesc, IFNULL(mgc.num,0) AS beforeNum, SUM((hi.after_count-hi.before_count) )AS afterNum, DATE_FORMAT(hi.create_time,'%Y-%m-%d') AS date, 0 num FROM inno72_supply_channel_history hi LEFT JOIN inno72_machine m ON m.id=hi.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code LEFT JOIN inno72_goods g ON g.id=hi.goods_id LEFT JOIN inno72_machine_goods_count mgc ON mgc.machine_id=m.id AND mgc.goods_id=hi.goods_id AND DATE_FORMAT(mgc.submit_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') WHERE hi.after_count>0 AND hi.after_count != hi.before_count AND LENGTH(hi.goods_id) >1 and hi.create_time > #{beginTime} and hi.create_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or g.name LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
GROUP BY DATE_FORMAT(hi.create_time,'%Y-%m-%d'),hi.machine_id,hi.goods_id ORDER BY hi.create_time DESC

SELECT s.id,m.id AS machineId,m.machine_code AS machineCode,DATE_FORMAT(s.create_time,'%Y-%m-%d') AS date, CONCAT(area.province,area.city,area.district,IFNULL(l.mall,""),IFNULL( l.name,'')) AS localeStr FROM inno72_supply_channel_order s LEFT JOIN inno72_machine m ON m.id=s.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.CODE = l.area_code WHERE 1=1 and LEFT(l.area_code,#{num}) = LEFT(#{code},#{num}) and s.create_time > #{beginTime} and s.create_time <= #{endTime}
AND (m.machine_code LIKE '%${keyword}%' or l.mall LIKE '%${keyword}%' or JSON_EXTRACT(l.tag, '$.tags') LIKE '%${keyword}%' or l.name LIKE '%${keyword}%' or area.province LIKE '%${keyword}%' or area.city LIKE '%${keyword}%' or area.district LIKE '%${keyword}%' )
GROUP BY s.machine_id,DATE_FORMAT(s.create_time,'%Y-%m-%d') ORDER BY s.create_time DESC
