mapper.select:
SELECT re.id, o.order_num AS orderNum, re.amount, re.status, re.phone, m.machine_code AS machineCode, CONCAT(area.province,area.city, area.district,IFNULL( l.mall,""),IFNULL( l.name,"")) AS local, c.channel_name AS channelName, i.`name` AS activityName, re.refund_num AS refundNum, date_format( re.create_time, '%Y-%m-%d %H:%i:%s' ) AS createTime, re.reason, re.audit_reason AS auditReason, re.refund_msg AS refundMsg, re.remark FROM inno72_order_refund re LEFT JOIN inno72_order o ON o.id = re.order_id LEFT JOIN inno72_machine m ON m.id = o.machine_id LEFT JOIN inno72_locale l ON l.id = m.locale_id LEFT JOIN inno72_admin_area area ON area.`code`=l.area_code LEFT JOIN inno72_channel c ON c.id = o.channel_id LEFT JOIN inno72_interact i ON i.id = o.inno72_activity_id
and re.audit_status =#{auditStatus} and re.status =#{status} and re.create_time >= #{beginTime} and re.create_time <= #{endTime}
and (m.machine_code like CONCAT('%','${keyword}','%') or o.order_num like CONCAT('%','${keyword}','%') or i.`name` like CONCAT('%','${keyword}','%') or re.refund_num like CONCAT('%','${keyword}','%') )
