mapper.select:
SELECT ap.id, ap.activity_id, ap.game_id, ap.start_time, ap.end_time, a.name AS activityName, ap.remark, ap.is_delete, ap.create_time, ap.update_time FROM inno72_activity_plan ap LEFT JOIN inno72_activity a ON a.id = ap.activity_id LEFT JOIN inno72_activity_plan_machine apm ON apm.activity_plan_id=ap.id LEFT JOIN inno72_machine m ON m.id = apm.machine_id LEFT JOIN inno72_locale locale ON locale.id = m.locale_id WHERE 1=1 and a.type=#{type} and LEFT(locale.area_code,#{num}) =#{code}
AND ( #{startTime} <= ap.end_time AND #{endTime} >= ap.start_time )
and ap.is_delete = 0 and NOW() < ap.end_time and ap.is_delete = 0 and NOW() < ap.start_time and ap.is_delete = 0 and (NOW() BETWEEN ap.start_time and ap.end_time ) and ap.is_delete != 1 and (NOW() > ap.end_time or ap.is_delete = 2) and 9=9 GROUP BY ap.id ORDER BY ap.start_time

SELECT machine_id FROM inno72_activity_plan ap
LEFT JOIN inno72_activity_plan_machine apm ON apm.activity_plan_id = ap.id
where is_delete =0
and ap.id = #{planId}  
and ap.id != #{noPlanId}  

	and ( ap.start_time <= #{endTime} AND ap.end_time >= #{startTime} )
