mapper.select:
select t1.id machineId, t1.machine_code machineCode, t1.machine_name machineName, CONCAT( t5.province, t5.city, t5.district, if(t5.circle='其他' or t5.circle is null,'',t5.circle),IFNULL(t4.mall,""),IFNULL( t4.name,'') ) AS localeStr, t3.* from inno72_machine t1 left join inno72_check_user_machine t2 on t1.id = t2.machine_id left join inno72_check_sign_in t3 on t2.machine_id=t3.machine_id and date_format(t3.create_time,'%Y%M%D') = date_format(CURRENT_DATE,'%Y%M%D') and t3.check_user_id=#{checkUserId,jdbcType=VARCHAR} and t3.status=0 LEFT JOIN inno72_locale t4 ON t4.id = t1.locale_id LEFT JOIN inno72_admin_area t5 ON t5.CODE = t4.area_code where t2.check_user_id = #{checkUserId}
and (t1.machine_code like '%${keyword}%' or t5.province like '%${keyword}%' or t5.city like '%${keyword}%' or t5.district like '%${keyword}%' or t5.circle like '%${keyword}%' or t4.mall like '%${keyword}%' or t4.name like '%${keyword}%' )
order by t3.create_time desc
