mapper.select:
select m.*, cu.id checkUserId, cu.name checkUserName, cu.phone checkUserPhone, CONCAT( aa.province, aa.city, aa.district, if(aa.circle='其他' or aa.circle is null,'',aa.circle),IFNULL(l.mall,""),IFNULL( l.name,'') ) AS localeStr from inno72_machine m inner join inno72_check_user_machine cum on m.id=cum.machine_id inner join inno72_check_user cu on cum.check_user_id=cu.id inner join inno72_locale l on l.id=m.locale_id inner join inno72_admin_area aa on aa.code = l.area_code where m.machine_status=4 and cu.id=#{checkUserId,jdbcType=VARCHAR}
AND
m.id = #{item,jdbcType=VARCHAR}
