mapper.select:
select sco.id batch_no, sco.machine_id, sco.create_time, m.machine_code, CONCAT( aa.province, aa.city, aa.district, if(aa.circle='其他' or aa.circle is null,'',aa.circle),IFNULL(l.mall,""),IFNULL( l.name,'') ) AS localeStr from inno72_supply_channel_order sco inner join inno72_machine m on sco.machine_id=m.id inner join inno72_check_user_machine cum on cum.machine_id=m.id inner join inno72_locale l ON l.id = m.locale_id inner join inno72_admin_area aa ON aa.CODE = l.area_code where cum.check_user_id=#{checkUserId}
and ( m.machine_code like CONCAT('%','${keyword}','%') OR aa.circle like CONCAT('%','${keyword}','%') )
and sco.create_time between #{beginTime} and #{endTime}
order by sco.create_time desc
