mapper.select:
select sco.id batchNo, sco.machine_id, sco.create_time, m.machine_code, CONCAT( aa.province, aa.city, aa.district, if(aa.circle='其他' or aa.circle is null,'',aa.circle),IFNULL(l.mall,""),IFNULL( l.name,'') ) AS localeStr from inno72_supply_channel_order sco inner join inno72_machine m on sco.machine_id=m.id inner join inno72_check_user_machine cum on m.id=cum.machine_id inner join inno72_locale l on l.id = m.locale_id inner join inno72_admin_area aa on aa.code = l.area_code where cum.check_user_id=#{checkUserId}
and ( m.machine_code like CONCAT('%','${keyword}','%') or aa.circle like CONCAT('%','${keyword}','%') or aa.district like CONCAT('%','${keyword}','%') )
and sco.create_time between #{beginTime} and #{endTime}
order by sco.create_time desc

select co.create_time, DATE_FORMAT(co.create_time,'%Y-%m-%d') creatDate, co.machine_id, m.machine_code from inno72_supply_channel_order co inner join inno72_machine m on co.machine_id=m.id inner join inno72_check_user_machine cum on m.id=cum.machine_id where co.user_id=#{checkUserId}
and m.id = #{machineId}
and DATE_FORMAT(co.create_time,'%Y%M') = DATE_FORMAT(#{findTime},'%Y%M')
and DATE_FORMAT(co.create_time,'%Y%M') = DATE_FORMAT(CURRENT_DATE,'%Y%M')
group by DATE_FORMAT(co.create_time,'%Y%M%D') order by co.create_time
