mapper.select:
SELECT o.id, o.order_num AS orderNum, g.`name` AS goodsName, g.`code` goodsCode, m.merchant_name AS merchantName, o.sender, o.receiver, o.number, o.creater, o.`status`, date_format( o.create_time, '%Y-%m-%d %H:%i:%s' ) AS createTime, IFNULL(e.expressNum,0) AS expressNum FROM inno72_store_order o LEFT JOIN inno72_goods g on g.id=o.goods LEFT JOIN inno72_merchant m ON m.id =o.merchant LEFT JOIN ( SELECT se.order_id,COUNT(1) AS expressNum FROM inno72_store_express se GROUP BY se.order_id ) e ON e.order_id = o.id LEFT JOIN inno72_storekeeper_storte ss ON ss.store_id=o.send_id WHERE o.order_type=1 and ss.storekeeper_id = #{userId} and o.status =#{status} and o.create_time >= #{beginTime} and o.create_time <= #{endTime}
and (o.sender like CONCAT('%','${keyword}','%') or o.receiver like CONCAT('%','${keyword}','%') or o.creater like CONCAT('%','${keyword}','%') or g.code like CONCAT('%','${keyword}','%') or g.name like CONCAT('%','${keyword}','%') or m.merchant_name like CONCAT('%','${keyword}','%') )
ORDER BY o.create_time DESC

SELECT o.id, o.order_num AS orderNum, g.`name` AS goodsName, g.`code` goodsCode, m.merchant_name AS merchantName, o.sender, o.receiver, o.number, o.receive_number AS receiveNumber, o.`status`, date_format( o.receive_time, '%Y-%m-%d %H:%i:%s' ) AS receiveTime, e.rExpressNum,e.expressNum,e.keeper FROM inno72_store_order o LEFT JOIN inno72_goods g on g.id=o.goods LEFT JOIN inno72_merchant m ON m.id =o.merchant LEFT JOIN ( SELECT se.order_id, COUNT( IF ( se.STATUS = 1, se.id, NULL ) ) AS rExpressNum, COUNT(1) AS expressNum,GROUP_CONCAT(se.receiver) as keeper FROM inno72_store_express se GROUP BY se.order_id ) e ON e.order_id = o.id LEFT JOIN inno72_storekeeper_storte ss ON ss.store_id=o.receive_id WHERE o.receive_type=2 and ss.storekeeper_id = #{userId} and o.status =#{status} and o.create_time >= #{beginTime} and o.create_time <= #{endTime}
and (o.sender like CONCAT('%','${keyword}','%') or o.receiver like CONCAT('%','${keyword}','%') or g.code like CONCAT('%','${keyword}','%') or g.name like CONCAT('%','${keyword}','%') or m.merchant_name like CONCAT('%','${keyword}','%') )
ORDER BY o.update_time DESC
